        public override void FillDataBaseStoredProcedures(XsdDataBase.TBL_ObjectDataTable nObjectTable, XsdDataBase.TBL_ConnectionRow nConnection, List<string> nSchemaFilter)
        {
            try
            {
                var sql = new StringBuilder();
                sql.AppendLine(@"SELECT sys_context('userenv', 'DB_NAME') AS routine_catalog ,u.name AS routine_schema, o.name  AS routine_name,");
                sql.AppendLine(@"'PROCEDURE' routine_type, DECODE(o.status, 1, 'V', 'I') status , 'StoredProcedure' AS generic_type");
                sql.AppendLine(@"FROM   sys.obj$ o, sys.USER$ u");
                sql.AppendLine(@"WHERE(o.TYPE# = 7)");
                sql.AppendLine(@"AND    o.owner# = u.USER#");
                sql.AppendLine(@"AND    o.linkname IS NULL");
                sql.AppendLine(@"AND    o.name NOT IN ('ADD_JOB_HISTORY','APEX','AW_DROP_PROC','DBMS_FEATURE_PARTITION_SYSTEM','DBMS_FEATURE_PARTITION_USER','DBMS_FEATURE_PLSQL_NATIVE','DBMS_FEATURE_RAC','DBMS_FEATURE_REGISTER_ALLFEAT','DBMS_FEATURE_REGISTER_ALLHWM','DBMS_FEATURE_TEST_PROC_1','DBMS_FEATURE_TEST_PROC_2','DBMS_FEATURE_TEST_PROC_3','DBMS_FEATURE_TEST_PROC_4','DBMS_FEATURE_TEST_PROC_5','DBMS_LOGMNR_FFVTOLOGMNRT','DBMS_LOGMNR_OCTOLOGMNRT','DEVELOPMENT_SERVICE_HOME','DRI_MOVE_CTXSYS','F','HTMLDB','HTMLDB_ADMIN','HTMLDB_LOGIN','LOAD_UNDO_STAT','LOGMNR_GTLO3','LOGMNR_KRVRDA_TEST_APPLY','LOGMNR_KRVRDLUID3','LOGMNR_KRVRDREPDICT3','ODCICOLINFODUMP','ODCIENVDUMP','ODCIINDEXALTEROPTIONDUMP','ODCIINDEXCALLPROPERTYDUMP','ODCIINDEXINFODUMP','ODCIINDEXINFOFLAGSDUMP','ODCIPREDINFODUMP','ODCIQUERYINFODUMP','ODCISTATSOPTIONSDUMP','ODCITABFUNCINFODUMP','ORA$GRANT_SYS_SELECT','ORA$_SYS_REP_AUTH','P','PSTUB','PSTUBT','RESET_UNDO_STAT','SECURE_DML','SETMODFLG','SET_TABLESPACE','SUBPTXT','SUBPTXT2','SYNCRN','VALIDATE_CONTEXT','WWV_CREATE_FORUM_DEMO_SCRIPT','WWV_FLOW_COPY','WWV_FLOW_COPY_BUTTON','WWV_FLOW_COPY_LOV','WWV_FLOW_COPY_PAGE_ITEM','WWV_FLOW_INIT_HTP_BUFFER','WWV_FLOW_SEED_TRANSLATIONS','WWV_FLOW_SYNC_TRANSLATIONS','XDB_DATASTORE_PROC','XDB$INITXDBSCHEMA','XDB$PATCHUPDELETESCHEMA','XDB$PATCHUPSCHEMA','Z')");
                sql.AppendLine(@"UNION SELECT sys_context('userenv', 'DB_NAME') AS routine_catalog , U.NAME AS routine_schema, o.name || '.' || pi.procedurename AS routine_name,");
                sql.AppendLine(@"'PACKAGE' routine_type, DECODE(o.status, 1, 'V', 'I') status, 'StoredProcedure' AS generic_type");
