        public override void FillDataBaseParameters(XsdDataBase.TBL_FieldDataTable nFieldTable, XsdDataBase.TBL_ObjectRow nTable)
        {
            try
            {
                string sql = "";

                if (nTable.Object_Name.IndexOf(".") > 0)
                {
                    string package = nTable.Object_Name.Split('.')[0];
                    string proc = nTable.Object_Name.Split('.')[1];

                    sql = @"SELECT argument_name AS parameter_name ,  'YES' is_nullable , data_type , data_length AS character_maximum_length," + ControlChars.CrLf +
                            "data_precision AS numeric_precision , data_scale AS numeric_scale , pls_type udt_name , sequence ordinal_position , in_out parameter_mode" + ControlChars.CrLf +
                            "FROM all_arguments WHERE owner = '" + nTable.Schema_Name + "' AND package_name = '" + package + "' AND object_name = '" + proc + "'" + ControlChars.CrLf +
                            "ORDER BY sequence";
                }
                else
                {
                    sql = @"SELECT argument_name AS parameter_name ,  'YES' is_nullable , data_type , data_length AS character_maximum_length," + ControlChars.CrLf +
                            "data_precision AS numeric_precision , data_scale AS numeric_scale , pls_type udt_name , sequence ordinal_position , in_out parameter_mode" + ControlChars.CrLf +
                            "FROM all_arguments WHERE owner = '" + nTable.Schema_Name + "' AND object_name = '" + nTable.Object_Name + "'" + ControlChars.CrLf +
                            "ORDER BY sequence";
                }

                DataTable table = ExecuteQueryGet(sql);

                for (int i = 0; i < table.Rows.Count; i++)
                {
                    DataRow row = table.Rows[i];
                    nFieldTable.AddTBL_FieldRow(nTable, row["parameter_name"].ToString(), GetGenericParameterType(row["data_type"].ToString()).ToString(), row["data_type"].ToString(), (row["is_nullable"].ToString().ToUpper() != "NO"), GetParameterSize(ConvertToInt(row["character_maximum_length"]), row["data_type"].ToString()), GetParameterPresicion(ConvertToByte(row["numeric_precision"]), row["data_type"].ToString()), GetParameterScale(ConvertToByte(row["numeric_scale"]), row["data_type"].ToString()), row["ordinal_position"].ToString(), GetGenericParameterDirection(row["parameter_mode"].ToString()));
                }

            }
            catch (Exception ex)
            {
                throw new Exception("No fue posible obtener el listado de columnas " + base.Connection.DataSource + " Tabla = " + nTable.Object_Name + ", " + ex.Message, ex);
            }
        }

