        public override DbType GetGenericParameterType(string nDataBaseParameterType)
        {
            switch (nDataBaseParameterType.ToUpper())
            {
                case "UNDEFINED":
                case "BLOB":
                    return DbType.Binary;

                case "DATE":
                    return DbType.Date;

                case "FLOAT":
                    return DbType.Double;

                case "LONG":
                case "LONG RAW":
                    return DbType.Int64;

                case "NUMBER":
                    return DbType.VarNumeric;

                case "RAW":
                case "SDO_TOPO_GEOMETRY_LAYER_ARRAY":
                case "SDO_DIM_ARRAY":
                case "SDO_GEOMETRY":
                    return DbType.Binary;

                case "TIMESTAMP(3)":
                    return DbType.Date;

                case "TIMESTAMP(6)":
                    return DbType.Date;

                case "TIMESTAMP(6) WITH TIME ZONE":
                    return DbType.Date;

                case "TIMESTAMP(9)":
                    return DbType.Date;

                case "TIMESTAMP(9) WITH TIME ZONE":
                    return DbType.Date;

                case "ROWID":
                case "NVARCHAR2":
                case "NCHAR":
                case "NCLOB":
                case "CHAR":
                case "CLOB":
                case "VARCHAR2":
                case "XMLTYPE":
                    return DbType.String;

                case "CURSOR":
                case "REF CURSOR":
                    return DbType.Object;

                default:
                    return DbType.Object;

            }
        }

        public override string GetGenericParameterDirection(string nDataBaseParameterDirection)
        {
            //TODO Realizar proceso de conversion de parametro de Oracle a generico
            switch (nDataBaseParameterDirection.ToUpper())
            {
                case "IN":
