            }
        }

        public override DbParameter ConfigureParameter(DbParameter param, Parameter nParameter)
        {
            var p = (OracleParameter)param;

            switch (p.DbType)
            {

                case DbType.Binary:
                    p.OracleType = OracleType.Blob;
                    break;

                case DbType.Byte:
                    p.OracleType = OracleType.Byte;
                    break;

                case DbType.DateTime:
                case DbType.Date:
                case DbType.DateTime2:
                case DbType.DateTimeOffset:
                    p.OracleType = OracleType.DateTime;
                    break;

                case DbType.Currency:
                case DbType.Decimal:
                case DbType.UInt64:
                case DbType.VarNumeric:
                    p.OracleType = OracleType.Number;
                    break;

                case DbType.SByte:
                case DbType.Int16:
                case DbType.UInt16:
                    p.OracleType = OracleType.Int16;
                    break;

                case DbType.Int32:
                case DbType.UInt32:
                    p.OracleType = OracleType.Int32;
                    break;

                case DbType.Int64:
                    p.OracleType = OracleType.Double;
                    break;

                case DbType.AnsiString:
                case DbType.AnsiStringFixedLength:
                case DbType.Boolean:
                case DbType.Object:
                case DbType.StringFixedLength:
                case DbType.Guid:
                case DbType.Xml:
                case DbType.String:
                    p.OracleType = OracleType.VarChar;
                    break;

                case DbType.Single:
                    p.OracleType = OracleType.Double;
                    break;

                case DbType.Time:
                    p.OracleType = OracleType.Timestamp;
                    break;
            }

            p.IsNullable = nParameter.IsNullable;

            if (nParameter.SpecificType.ToUpper() == "CURSOR" || nParameter.SpecificType.ToUpper() == "REF CURSOR")
            {
                p.OracleType = OracleType.Cursor;
                p.DbType = DbType.Object;
            }

            p = new OracleParameter(nParameter.Name, p.OracleType, nParameter.MaxLength, p.Direction, nParameter.IsNullable, nParameter.Precision, nParameter.Scale, "", DataRowVersion.Current, param.Value);

            if (param.Value == null)
            {
                p.Value = DBNull.Value;
            }

            return p;
        }

        public override string FormatToDatabaseStoredProcedureName(string nSchemaName, string nTableName)
        {
            if (nSchemaName != "")
                return Identifier_Procedure_Prefix + nSchemaName + Identifier_Procedure_Postfix + Identifier_Schema_Separator + Identifier_Procedure_Prefix + nTableName + Identifier_Procedure_Postfix;
            else
                return Identifier_Procedure_Prefix + nTableName + Identifier_Procedure_Postfix;
        }

        public override string ConvertSqlSelectToMaxRows(string sql, int nMaxRows)
        {
            if (nMaxRows > 0)
                return " SELECT * FROM (" + sql + ") TAB_TEMP_MAX_ROWS WHERE ROWNUM < " + nMaxRows;
            else
                return sql;
        }

        public override string ConvertSqlSelectToOrderBy(string sql, ColumnEnumList GroupByParams)
        {
            if (GroupByParams != null && GroupByParams.Count > 0)
                return sql + " ORDER BY " + GroupByParams.getOrderByParams(this);
            else
                return sql;
        }

        public override string GetDataFilterString(DataFilterType type)
        {
            switch (type)
            {
                case DataFilterType.AD: return "[A-D]**";
                case DataFilterType.EH: return "[E-H]**";
                case DataFilterType.IL: return "[I-L]**";
                case DataFilterType.MP: return "[M-P]**";
                case DataFilterType.QT: return "[Q-T]**";
                case DataFilterType.UZ: return "[U-Z]**";
                case DataFilterType._All: return "*";
                default:
                    return "";
            }
        }

